document.addEventListener("DOMContentLoaded",(function(){document.getElementById("contact").addEventListener("submit",(function(e){e.preventDefault();let t=new FormData(this);fetch("library/mail.php",{method:"POST",body:t}).then((e=>e.json())).then((e=>{e.success?(Swal.fire({title:"Message envoyé !",text:"Nous avons bien reçu votre message et nous vous répondrons rapidement.",icon:"success",confirmButtonText:"OK"}),document.getElementById("contact").reset()):Swal.fire({title:"Erreur",text:e.error||"Une erreur est survenue, veuillez réessayer.",icon:"error",confirmButtonText:"OK"})})).catch((e=>{Swal.fire({title:"Erreur serveur",text:"Impossible de soumettre le formulaire pour le moment.",icon:"error",confirmButtonText:"OK"})}))}))}));